mapper.update:
UPDATE lgw_novel
SET type_code=#{typeCode},
file_name=#{fileName},
title=#{title},
resource_id=#{resourceId}
WHERE novel_id=#{novelId}

mapper.select:
SELECT
FROM lgw_novel
ORDER BY create_date DESC limit #{condition.offset},#{condition.pageSize}

SELECT

FROM lgw_novel
WHERE novel_id=(SELECT MAX(novel_id) FROM lgw_novel WHERE  novel_id < #{condition.novelId}
AND type_code= #{condition.type}
ORDER BY create_date DESC LIMIT 1)
UNION ALL
SELECT

FROM lgw_novel
WHERE novel_id=(SELECT MIN(novel_id) FROM lgw_novel WHERE  novel_id > #{condition.novelId}
AND type_code=#{condition.type}
ORDER BY create_date DESC LIMIT 1)
