mapper.insert:
insert into lgw_picture(name,type_code,img,resource_id,create_date)
values(#{pictureId},#{name},#{typeCode},#{resourceId},#{createDate});


    SELECT  LAST_INSERT_ID() AS pictureId

mapper.select:
SELECT
FROM lgw_picture
ORDER BY create_date DESC limit #{condition.offset},#{condition.pageSize}

SELECT

FROM lgw_picture
WHERE picture_id=(SELECT MAX(picture_id) FROM lgw_picture WHERE  picture_id < #{condition.id}
AND type_code= #{condition.type}
ORDER BY create_date DESC LIMIT 1)
UNION ALL
SELECT

FROM lgw_picture
WHERE picture_id=(SELECT MIN(picture_id) FROM lgw_picture WHERE  picture_id > #{condition.id}
AND type_code=#{condition.type}
ORDER BY create_date DESC LIMIT 1)
